#### Steps & Code to Reproduce
migration
public class RoomMigration implements RealmMigration {
    @Override
    public void migrate(DynamicRealm realm, long oldVersion, long newVersion) {
        RealmSchema schema = realm.getSchema();
        if (oldVersion == 0) {
            // 
            RealmObjectSchema gun = schema.create("Gun");
            gun.addField("uid", String.class)
                    .addField("code", String.class)
                    .addField("name", String.class);

            RealmObjectSchema magazine = schema.create("Magazine");
            magazine.addField("uid", String.class, FieldAttribute.PRIMARY_KEY)
                    .addField("code", String.class)
                    .addField("name", String.class);
//
            RealmObjectSchema cabinet = schema.create("Cabinet");
            cabinet.addField("uid", String.class, FieldAttribute.PRIMARY_KEY)
                    .addField("code", String.class)
                    .addField("name", String.class)
                    .addField("lock_code", String.class)
                    .addField("type", int.class)
                    .addRealmListField("gun_list", gun)
                    .addRealmListField("magazine_list", magazine);

            schema.create("Room")
                    .addField("uid", String.class, FieldAttribute.PRIMARY_KEY)
                    .addField("code", String.class)
                    .addField("name", String.class)
                    .addField("lock_code", String.class)
                    .addField("type",int.class)
                    .addField("temperature",String.class)
                    .addField("humidity",String.class)
                    .addField("smoke",String.class)
                    .addField("first_room", boolean.class)
                    .addRealmListField("cabinet_list", cabinet);
            oldVersion++;
        }
    }
}



