RealmManager:

    public class RealmManager {
    
        private final ThreadLocal<Realm> localRealms = new ThreadLocal<>();
    
        public RealmManager() {
            checkDefaultConfiguration();
        }
    
        public Realm open() {
    
            Realm realm = Realm.getDefaultInstance();
    
            if (!checkRealm(localRealms.get())) {
                localRealms.set(realm);
            }
    
            realm.refresh();
    
            return realm;
        }
    
        public Realm getLocalInstance() {
    
            Realm realm = localRealms.get();
    
            if (!checkRealm(realm)) {
                realm = open();
            }
    
            realm.refresh();
    
            return realm;
        }
    
        public void close(Realm realm) {
    
            if (!checkRealm(realm)) {
                return;
            }
    
            realm.close();
            controlLocalInstanceCount();
        }
    
        public void close() {
    
            Realm realm = localRealms.get();
            close(realm);
        }
    
        private static void checkDefaultConfiguration() {
    
            if (Realm.getDefaultConfiguration() == null) {
                new RealmEncryptionHelper().setDefaultConfiguration();
            }
        }
    
        public boolean checkRealm(Realm realm) {
            return realm != null && !realm.isClosed();
        }
    
        private void controlLocalInstanceCount() {
    
            // noinspection ConstantConditions
            int localInstanceCount = Realm.getLocalInstanceCount(Realm.getDefaultConfiguration());
            // noinspection ConstantConditions
    //        int globalInstanceCount = Realm.getGlobalInstanceCount(Realm.getDefaultConfiguration());
    
            // noinspection ConstantConditions
            if (localInstanceCount <= 0) {
                localRealms.set(null);
            }
        }
    
    
    }

RealmThreadExecutor:

    public class RealmThreadExecutor {
    
        private static String TAG = RealmThreadExecutor.class.getSimpleName();
    
        private static final BlockingQueue<Runnable> sPoolWorkQueue = new LinkedBlockingQueue<>();
        /**
         * An {@link Executor} that can be used to execute tasks in parallel.
         */
        private static ThreadPoolExecutor threadPoolExecutor;
    
        public void addRealmRunnable(RealmRunnable realmRunnable) {
    
            if (sPoolWorkQueue.size() == 0) {
                getPoolExecutor().execute(realmRunnable);
            } else {
                sPoolWorkQueue.add(realmRunnable);
            }
        }
    
        private static ThreadPoolExecutor getPoolExecutor() {
    
            if (threadPoolExecutor == null) {
                threadPoolExecutor = new ParallelExecutor();
            }
            return threadPoolExecutor;
        }
    
        private static class ParallelExecutor extends ThreadPoolExecutor {
    
            private RealmManager realmManager;
    
            private ParallelExecutor() {
                super(1, 1, 0L, TimeUnit.MILLISECONDS, sPoolWorkQueue, new BackgroundThreadFactory());
                realmManager = new RealmManager();
            }
    
            @Override
            protected void beforeExecute(Thread thread, Runnable runnable) {
                super.beforeExecute(thread, runnable);
    
                Realm realm = realmManager.open();
    
                RealmRunnable realmRunnable = (RealmRunnable) runnable;
                realmRunnable.setRealm(realm);
                realmRunnable.setThread(thread);
            }
    
            @Override
            protected void afterExecute(Runnable runnable, Throwable throwable) {
                super.afterExecute(runnable, throwable);
    
                RealmRunnable realmRunnable = (RealmRunnable) runnable;
                Realm realm = realmRunnable.getRealm();
                realmManager.close(realm);
    
                Thread thread = realmRunnable.getThread();
                thread.interrupt();
    
                scheduleNext();
            }
    
            private synchronized void scheduleNext() {
                if (sPoolWorkQueue.size() != 0) {
                    getPoolExecutor().execute(sPoolWorkQueue.poll());
                }
            }
    
        }
    
        private static class BackgroundThreadFactory implements ThreadFactory {
    
            @Override
            public Thread newThread(@NonNull Runnable runnable) {
                Thread thread = new Thread(runnable);
                thread.setName("realmPoolThread");
                thread.setPriority(Process.THREAD_PRIORITY_BACKGROUND);
    
                return thread;
            }
        }

defaultConfiguration : 

    public class RealmEncryptionHelper {
    
        private static final String ENCRYPTION_FILE_PREFIX = "e_";
    
        public void setDefaultConfiguration() {
    
            String DEFAULT_REALM_NAME = "a.realm";
    
            Realm.init(MyApplication.getContext());
    
            RealmConfiguration.Builder realmConfigurationBuilder = new RealmConfiguration.Builder()
                    .name(DEFAULT_REALM_NAME)
                    .schemaVersion(Constants.realmVersion)
                    .migration(new Migration());
    
            Realm.init(MyApplication.getContext());
            RealmConfiguration unencryptedConfig = realmConfigurationBuilder.build();
    
            RealmConfiguration encryptedConfig = new RealmConfiguration.Builder()
                    .name(ENCRYPTION_FILE_PREFIX + unencryptedConfig.getRealmFileName())
                    .schemaVersion(Constants.realmVersion) 
                    .migration(new Migration())
                    .encryptionKey(new RealmCreateKey().get())
                    .build();
    
            migrationIfNeeded(unencryptedConfig, encryptedConfig);
            Realm.setDefaultConfiguration(encryptedConfig);
        }
    
        private void migrationIfNeeded(RealmConfiguration unencryptedConfig, RealmConfiguration encryptedConfig) {
            File unencryptedFile = new File(unencryptedConfig.getPath());
            File encryptedFile = new File(encryptedConfig.getPath());
    
            Realm unencryptedRealm = null;
            if (!encryptedFile.exists() && unencryptedFile.exists()) {
                try {
                    unencryptedRealm = Realm.getInstance(unencryptedConfig);
                    unencryptedRealm.writeEncryptedCopyTo(encryptedFile, encryptedConfig.getEncryptionKey());
                } catch (Exception e) {
                    Mylog.printStackTrace("RealmEncryptionHelper migrationIfNeeded error", e);
                } finally {
                    if (unencryptedRealm != null) {
                        unencryptedRealm.close();
                    }
                }
            }
        }
    }

