package team.semicolon.amizeh.Model;

import android.os.Parcel;
import android.os.Parcelable;

import java.util.ArrayList;
import java.util.List;

import io.realm.RealmList;
import io.realm.RealmObject;
import io.realm.annotations.PrimaryKey;
import team.semicolon.amizeh.Interface.Projective;

public class Playlist extends RealmObject implements Projective, Parcelable {
    @PrimaryKey
    private String table_id;
    public String id;
    private String table;
    private String name;
    private String photoURL;
    private RealmList<Song> songs;
    private boolean isFollowed;

    public Playlist(){}

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getPhotoURL() {
        return photoURL;
    }

    public void setPhotoURL(String photoURL) {
        this.photoURL = photoURL;
    }

    public RealmList<Song> getSongs() {
        return songs;
    }

    public void setSongs(RealmList<Song> songs) {
        this.songs = songs;
    }

    public boolean isFollowed() {
        return isFollowed;
    }

    public void setFollowed(boolean followed) {
        isFollowed = followed;
    }

    public int getDuration() {
        int sum = 0;
        for (Song s : songs)
            sum += s.getDuration();
        return sum;
    }

    @Override
    public String getDrawable() {
        return getPhotoURL();
    }

    @Override
    public String getID() {
        return id;
    }

    public String getTable() {
        return table;
    }

    public void setTable(String table) {
        this.table = table;
        this.table_id=table+id;
    }

    protected Playlist(Parcel in) {
        id = in.readString();
        name = in.readString();
        photoURL = in.readString();
        if (in.readByte() == 0x01) {
            songs = new RealmList<>();
            in.readList(songs, Song.class.getClassLoader());
        } else {
            songs = null;
        }
        isFollowed = in.readByte() != 0x00;
    }

    @Override
    public int describeContents() {
        return 0;
    }


    @Override
    public void writeToParcel(Parcel dest, int flags) {
        dest.writeString(id);
        dest.writeString(name);
        dest.writeString(photoURL);
        if (songs == null) {
            dest.writeByte((byte) (0x00));
        } else {
            dest.writeByte((byte) (0x01));
            dest.writeList(songs);
        }
        dest.writeByte((byte) (isFollowed ? 0x01 : 0x00));
    }

    @SuppressWarnings("unused")
    public static final Parcelable.Creator<Playlist> CREATOR = new Parcelable.Creator<Playlist>() {
        @Override
        public Playlist createFromParcel(Parcel in) {
            return new Playlist(in);
        }

        @Override
        public Playlist[] newArray(int size) {
            return new Playlist[size];
        }
    };
}
