package** com.example.ashish.couchbase;
import android.content.Intent;
import android.support.annotation.NonNull;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.text.TextUtils;
import android.util.Log;
import android.view.View;
import android.view.View.OnClickListener;
import android.widget.Button;
import android.widget.Toast;

import com.example.ashish.couchbase.model.InventoryData;
import com.example.ashish.couchbase.model.User;

import io.realm.ObjectServerError;
import io.realm.Realm;
import io.realm.SyncConfiguration;
import io.realm.SyncCredentials;
import io.realm.SyncUser;

import static android.Manifest.permission.READ_CONTACTS;
import static com.example.ashish.couchbase.Constants.AUTH_URL;

/**
 * A login screen that offers login via email/password.
 */
public class LoginActivity extends AppCompatActivity {


    private static final int REQUEST_READ_CONTACTS = 0;


    private android.support.design.widget.TextInputLayout mEmailView, mPassTextView;
    private View mProgressView;
    private User user = null;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_login);
        // Set up the login form.

        mEmailView = findViewById(R.id.textInputEmail);
        mPassTextView = findViewById(R.id.textInputPassword);
        if (SyncUser.current() != null) {
            //close this activity
            finish();
            //opening profile activity
            startActivity(new Intent(getApplicationContext(), HomeActivity.class));
        }


        Button mEmailSignInButton = (Button) findViewById(R.id.email_sign_in_button);
        mEmailSignInButton.setOnClickListener(new OnClickListener() {
            @Override
            public void onClick(View view) {
                mProgressView.setVisibility(View.VISIBLE);
                userLogin();
            }
        });

        mProgressView = findViewById(R.id.login_progress);

    }

    private void userLogin() {
        String email = mEmailView.getEditText().getText().toString().trim();
        String password = mPassTextView.getEditText().getText().toString().trim();

        //checking if email and passwords are empty
        if (TextUtils.isEmpty(email)) {

            mEmailView.setError("Please enter email");
            // Toast.makeText(this,"Please enter email",Toast.LENGTH_LONG).show();
            mProgressView.setVisibility(View.GONE);
            return;
        }

        if (TextUtils.isEmpty(password)) {
            mPassTextView.setError("Please enter password");
            //Toast.makeText(this,"Please enter password",Toast.LENGTH_LONG).show();
            mProgressView.setVisibility(View.GONE);
            return;
        }


        SyncCredentials credentials = SyncCredentials.usernamePassword("sagarshirbhate", "Bits@123", false);

        SyncUser.logInAsync(credentials, Constants.AUTH_URL, new SyncUser.Callback<SyncUser>() {
            @Override
            public void onSuccess(@NonNull SyncUser syncUser) {
                System.out.println("onSuccess:" + syncUser.toString());

                SyncConfiguration config = new SyncConfiguration.Builder(syncUser, Constants.REAM_URL)
                   
                          .build();
                Realm.setDefaultConfiguration(config);

                Realm.getInstanceAsync(config, new Realm.Callback() {
                    @Override
                    public void onSuccess(@NonNull Realm realm) {
                        // Realm is now downloaded and ready. New changes to Realm will continue
                        // to be synchronized in the background as normal.
                        
                       // applcation class having static object  
                       Application.setRealmSuperInstance(Realm.getInstance(config));

                        user = realm.where(User.class).
                                equalTo("email", email)
                                .equalTo("password", password)
                                .findFirst();

                        if (user != null) {
                            mProgressView.setVisibility(View.GONE);


                            PreferenceManager.getInstance(LoginActivity.this).addID(user.getUserId());
                            PreferenceManager.getInstance(LoginActivity.this).addRole(user.getRole());

                            finish();
                            startActivity(new Intent(getApplicationContext(), HomeActivity.class));
                        } else {
                            mProgressView.setVisibility(View.GONE);
                            Toast.makeText(LoginActivity.this, "credential not match", Toast.LENGTH_LONG).show();
                        }
                    }
                    @Override
                    public void onError(@NonNull Throwable exception) {
                        super.onError(exception);
                        mProgressView.setVisibility(View.GONE);
                    }
                });
            }
            @Override
            public void onError(ObjectServerError error) {
            }
        });
    }
}
