`fun getAllVibrations(): Observable<RealmResults<ItemVibrator>> {
        val realm = Realm.getDefaultInstance()

        realm.beginTransaction()
        val realmResults: RealmResults<ItemVibrator> = realm.where(ItemVibrator::class.java).findAll()
        realm.commitTransaction()

        return Observable.create<RealmResults<ItemVibrator>>(object : ObservableOnSubscribe<RealmResults<ItemVibrator>> {
            @Throws(Exception::class)
            override fun subscribe(emitter: ObservableEmitter<RealmResults<ItemVibrator>>) {
                try {
                    emitter.onNext(realmResults)
                    emitter.onComplete()
                } catch (e: Exception) {
                    emitter.onError(e)
                }
            }
        })
    }

