`@Parcel(implementations = {io.realm.com_trip_models_trip_TripStatusRealmProxy.class},
        value = Parcel.Serialization.BEAN,analyze = {TripStatus.class})
public class TripStatus extends RealmObject {

    @PrimaryKey
    Integer id;

    String passengerName;

    Integer flightStepOrdinal;

    Integer currentFlight;

    Date updatedAt;
    @ParcelPropertyConverter(RealmListParcelConverter.class)
    RealmList<Flight> flights = new RealmList<>();

    static Realm realm = Realm.getDefaultInstance();

    //Empty constructor for Parceler
    public TripStatus() {
        super();
    }

    public TripStatus(RealmList<Flight> flights) {
        this.flights = flights;
    }

    public Integer getId() {
        return id;
    }

    public void setId(Integer id) {
        id = (int) UUID.randomUUID().getMostSignificantBits();
        this.id = id;
    }

    public String getPassengerName() {
        return passengerName;
    }

    public void setPassengerName(String passengerName) {
        this.passengerName = passengerName;
    }


    public RealmList<Flight> getFlights() {
        if (flights == null || flights.size() < 1) {
            RealmResults<Flight> flightRealmResults = realm.where(Flight.class)
                    .equalTo("id", id).findAll()
                    .sort("arrivalTimeUTC", Sort.ASCENDING);

            flights.addAll(flightRealmResults);
        }
        return flights;
    }
    @ParcelPropertyConverter(RealmListParcelConverter.class)
    public void setFlights(RealmList<Flight> flights) {
        this.flights = flights;
    }


    public Integer getCurrentFlight() {
        return currentFlight;
    }

    public Flight getCurrentFlightInstance() {
        return flights.get(currentFlight);
    }

    public void setCurrentFlight(Integer currentFlight) {
        this.currentFlight = currentFlight;
    }

    public Integer getFlightStepOrdinal() {
        return flightStepOrdinal;
    }

    public void setFlightStepOrdinal(Integer flightStepOrdinal) {
        this.flightStepOrdinal = flightStepOrdinal;
    }

    public Date getUpdatedAt() {
        return updatedAt;
    }

    public void setUpdatedAt(Date updatedAt) {
        this.updatedAt = updatedAt;
    }

    public static TripStatus get(Integer id) {
        return realm.where(TripStatus.class).equalTo("id", id).findFirst();
    }

    public static RealmList<TripStatus> getAll() {
        RealmResults<TripStatus> realmResults = realm.where(TripStatus.class).findAll();
        RealmList<TripStatus> realmList = new RealmList<>();
        realmList.addAll(realmResults);
        return realmList;
    }

    public static void persist(TripStatus tripStatus) {
        for (Flight f : tripStatus.getFlights()) {



            realm.beginTransaction();

            realm.copyToRealm(f);

            realm.commitTransaction();


        }
        tripStatus.setUpdatedAt(new Date());
        realm.copyToRealm(tripStatus);
    }



    public static TripStatus saveOrUpdateTrip(TripStatus trip, FlightStatus flight, String seatNo, Appendix appendix) {
        Flight f = new Flight(flight, seatNo, appendix);
        if (trip == null || trip.getId() == null) {
            return createNewTrip(new RealmList<Flight>(f));
        }
        return updateTrip(trip, f);
    }

    public static TripStatus createTrip(RealmList<ScheduledFlight> scheduleFlights, Appendix appendix) {
        RealmList<Flight> flights = new RealmList<Flight>();
        for (ScheduledFlight s : scheduleFlights) {
            flights.add(new Flight(s, appendix));
        }
        return createNewTrip(flights);
    }

    public static void deleteTrip(TripStatus trip) {

        realm.beginTransaction();

        RealmQuery<TripStatus> realmQuery = realm.where(TripStatus.class);
        realmQuery.equalTo("id", trip.id);

        final TripStatus tripStatus = realmQuery.findFirst();

        if(tripStatus!=null){
            realm.executeTransaction(new Realm.Transaction() {
                @Override
                public void execute(Realm realm) {
                    tripStatus.deleteFromRealm();
                }
            });
        }

        realm.commitTransaction();
    }


    private static TripStatus updateTrip(TripStatus trip, Flight flight) {
        boolean isNewFlight = true;
        for (int i = 0; i < trip.getFlights().size(); i++) {
            Flight f = trip.getFlights().get(i);
            if (f.getFlightNumber() == flight.getFlightNumber() && f.getDepartureAirportIATA() == flight.getDepartureAirportIATA()) {
                Flight.mergeFlights(f, flight);
                try{
                realm.beginTransaction();

                realm.copyToRealm(f);

                realm.commitTransaction();}finally {
                    realm.close();
                }
                isNewFlight = false;
                break;
            }
        }
        if (isNewFlight) {
            flight.setTripId(trip.getId());
            try {
            realm.beginTransaction();

            realm.copyToRealm(flight);

            realm.commitTransaction();
            trip.getFlights().add(flight);}finally {
                realm.close();
            }
        }
        trip.setUpdatedAt(new Date());
        realm.beginTransaction();

        realm.copyToRealmOrUpdate(trip);

        realm.commitTransaction();
        return trip;
    }

    private static TripStatus createNewTrip(RealmList<Flight> flights) {
        TripStatus trip = new TripStatus();
        trip.setCurrentFlight(0);
        trip.setUpdatedAt(new Date());
        try{
        realm.beginTransaction();

        realm.copyToRealmOrUpdate(trip);

        realm.commitTransaction();}finally {
            realm.close();
        }
        int i = 0;
        for (Flight f : flights) {
            f.setTripId(trip.getId());
            try{

            realm.beginTransaction();

            realm.copyToRealmOrUpdate(f);

            realm.commitTransaction();}finally {
                realm.close();
            }
        }
        return TripStatus.get(trip.getId());
    }
}
