	fun provideCommonRealmBuilder(realmConfigDescriptor: RealmConfigDescriptor): RealmConfiguration.Builder {
		return RealmConfiguration.Builder()
			.name(realmConfigDescriptor.databaseName)
			.schemaVersion(realmConfigDescriptor.version)
			.modules(realmConfigDescriptor.baseModule, *realmConfigDescriptor.additionalModules)
