//Model
public class Group extends RealmObject {

    public static final String PK = "groupId";
    public static final String FIELD_SPORT = "sportId";
    public static final String FIELD_TYPE = "type";
    public static final String FIELD_NAME = "name";

    public static final String TYPE_MANUAL = "M";
    public static final String TYPE_SMART = "S";

    @PrimaryKey
    private long groupId;
    private String name;
    private String type;
    private Integer count;
    @Index
    private long sportId;
    private RealmList<RealmLong> contactIds = new RealmList<>();

    public long getGroupId() {
        return groupId;
    }

    public void setGroupId(final long groupId) {
        this.groupId = groupId;
    }

    public String getName() {
        return name;
    }

    public void setName(final String name) {
        this.name = name;
    }

    public String getType() {
        return type;
    }

    public void setType(final String type) {
        this.type = type;
    }

    public Integer getCount() {
        return count;
    }

    public void setCount(final Integer count) {
        this.count = count;
    }

    public long getSportId() {
        return sportId;
    }

    public void setSportId(final long sportId) {
        this.sportId = sportId;
    }

    public RealmList<RealmLong> getContactIds() {
        return contactIds;
    }

    public void setContactIds(final RealmList<RealmLong> contactIds) {
        this.contactIds = contactIds;
    }

    @Override
    public String toString() {
        return "Group{" +
                "groupId=" + groupId +
                ", name='" + name + '\'' +
                ", type='" + type + '\'' +
                ", count=" + count +
                ", sportId=" + sportId +
                ", contactIds=" + contactIds +
                '}';
    }
}

public class RealmLong extends RealmObject {
    private long value;

    public RealmLong() {
    }

    public RealmLong(final long value) {
        this.value = value;
    }

    public long getValue() {
        return value;
    }

    public void setValue(long value) {
        this.value = value;
    }

    @Override
    public boolean equals(final Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;

        RealmLong realmLong = (RealmLong) o;

        return getValue() == realmLong.getValue();

    }

    @Override
    public int hashCode() {
        return (int) (getValue() ^ (getValue() >>> 32));
    }
}
