#### Code Sample

java

    // Realm Initialization in Application class
    private void initializeRealm(final int schemaVersion) {
        Realm.init(Application.this);
        RealmConfiguration realmConfiguration;
        realmConfiguration = new RealmConfiguration.Builder()
                .schemaVersion(schemaVersion)
                .migration(new Migration())
                .deleteRealmIfMigrationNeeded()
                .build();
        Realm.setDefaultConfiguration(realmConfiguration);
    }

    // Snippet which causes crash when call Realm.getDefaultInstance()
        [...]
    //check if Fabric is initialized
    if (Fabric.isInitialized()){
        try{
            //get a Realm instance, if exist Realm is initialized. If not catch the exception below
            Realm realm = Realm.getDefaultInstance();
            if (realm != null){
                //close Realm instance
                realm.close();
            }
            //start the FirstActivity
            Intent intent = new Intent(SplashActivity.this, FirstActivity.class);
            if (getIntent().getExtras() != null){
                intent.putExtras(getIntent().getExtras());
            }
            startActivity(intent);
            finish();
            overridePendingTransition(0, 0);
        }catch (IllegalStateException e){
            //Fabric is already initialized, log the exception to Crashlytics, and kill the Nowr process
            Crashlytics.logException(e);
            Process.killProcess(Process.myPid());
            System.exit(0);
        }
    }

