public void initSyncUser(final HashMap<String, String> data) {
        SyncUser.loginAsync(myCredentials, Constants.authenticationUrl, new SyncUser.Callback<SyncUser>() {
            @Override
            public void onSuccess(@NonNull final SyncUser result) {
                checkUser(data);
                Log.i("RealmHelper", "Login successful");
            }

            @Override
            public void onError(@NonNull ObjectServerError error) {
                Log.i("RealmHelper", "Login failed");
            }
        });
 }

