java
public void checkUser(final HashMap<String, String> data) {
        SyncConfiguration config = new SyncConfiguration.Builder(syncUser, Constants.realmUri)
                .schemaVersion(0)
                .build();

        Realm.getInstanceAsync(config, new Realm.Callback() {
            @Override
            public void onSuccess(@NonNull Realm realm) {
                //This realm is empty after removing app data
                AuthActivity.this.realm = realm;
                
                // Check if user already exists
                RealmQuery<User> query = realm.where(User.class);
                query.equalTo("id", data.get("id"));
                User u = query.findFirst();
                if (u == null) {
                    createUser(data);
                    Log.i("RealmHelper", "User did not exist");
                } else {
                    if (u.isValid()) {
                        startActivityMain();
                        Log.i("RealmHelper", "User did exist");
                    }
                }
            }
        });
 }

