// This is a simplified version of my code. // I get back data from the network in `step_1`, // update my Realm objects, // and load them again at `step_2`
data class SyncStep(val stepNumber: String, val name: String, val obs: () -> Observable<*>)
data class PostResult (val oldID : Long, val newID : Long, val typeOfResult: KClass<*>? = null)
// see below for the definition of syncPendingArrests val step2 = SyncStep("2", "Send Arrests", { syncPendingArrests(messages) })
// List of 'SyncStep' val stepsList = listOf(step1, step2, step3 /*, ... */ )
val stepsObservable : Observable<SyncStep> = Observable.fromIterable(stepsList)
stepsObservable .concatMap { elem: SyncStep -> Log.e("Rx", "$threadName concatMap: ${elem.stepNumber}: ${elem.name}")
updateProgress(elem.name) return@concatMap elem.obs() } .subscribeOn(Schedulers.io()) .observeOn(Schedulers.io()) .subscribeBy( onNext = { item -> })
private fun syncPendingArrests(messages: MutableList<String>) : Observable<PostResult> { Log.e("Rx", "$threadName syncPendingArrests(): Enter") val realm = Realm.getDefaultInstance()
// : Waiting before calling realm.refresh() seems necessary :( Thread.sleep(250) realm.refresh()
val pendingArrests = realm.where(Arrest::class.java) .equalTo("submitted", false) .findAll() .sort("id", Sort.DESCENDING) .toList()
