//detach from Realm, copy values to fields val detached = realm.copyFromRealm(pendingArrests) //pendingArrests.map { managed -> realm.copyFromRealm(managed) }
for (item: Arrest in detached) { Log.e("Rx", "$threadName syncPendingArrests(), id: ${item.id}, trap.id: ${item.trap?.id}, trap.name: ${item.trap?.name}") }
realm.close() return arrestManager.postObservationNew(detached) } `

