#### Code Sample


public class OperationListAdapter extends RecyclerView.Adapter<BindingHolder<ItemOperationListBinding>> {

    private Context context;
    private ArrayList<OperationListResponse> realmResults;

    public OperationListAdapter(Context context, ArrayList<OperationListResponse> data) {
        this.context = context;
        this.realmResults = data;
    }

    @Override
    public BindingHolder<ItemOperationListBinding> onCreateViewHolder(ViewGroup parent, int viewType) {
        LayoutInflater inflater = LayoutInflater.from(parent.getContext());
        ItemOperationListBinding binding = DataBindingUtil.inflate(inflater, R.layout.item_operation_list, parent, false);
        return new BindingHolder<>(binding);
    }

    @Override
    public void onBindViewHolder(BindingHolder<ItemOperationListBinding> holder, int position) {
        OperationListResponse operationListResponse = realmResults.get(position);

        OperationCardAdapter cardAdapter = new OperationCardAdapter(context, operationListResponse.getOperations());
        holder.binding.list.setAdapter(cardAdapter);
        holder.binding.list.setLayoutManager(new LinearLayoutManager(context));

        holder.binding.tvDay.setText(operationListResponse.getDay());
        holder.binding.tvDayOfWeek.setText(operationListResponse.getDayOfWeek());
        holder.binding.tvMonthYear.setText(String.format("%s %s", operationListResponse.getMonthName(), operationListResponse.getYear()));
        holder.binding.tvDateCost.setText(String.valueOf(operationListResponse.getCost()));
        holder.binding.tvDateCurrency.setText(operationListResponse.getDateCurrency());

        if(operationListResponse.getDateType() == OperationType.INCOME) {
            holder.binding.tvDateCost.setTextColor(context.getResources().getColor(R.color.green));
            holder.binding.tvDateCurrency.setTextColor(context.getResources().getColor(R.color.green));
        } else if (operationListResponse.getDateType() == OperationType.EXPENSE) {
            holder.binding.tvDateCost.setTextColor(context.getResources().getColor(R.color.red));
            holder.binding.tvDateCurrency.setTextColor(context.getResources().getColor(R.color.red));
        }
    }

    @Override
    public int getItemCount() {
        return realmResults.size();
    }
}

And nested adapter:

public class OperationCardAdapter extends RealmRecyclerViewAdapter<Operation, BindingHolder<ItemOperationCardBinding>> {

    private Context context;

    OperationCardAdapter(Context context, OrderedRealmCollection<Operation> operations) {
        super(operations, true);
        setHasStableIds(true);
        this.context = context;
    }

    @Override
    public BindingHolder<ItemOperationCardBinding> onCreateViewHolder(ViewGroup parent, int viewType) {
        LayoutInflater inflater = LayoutInflater.from(parent.getContext());
        ItemOperationCardBinding binding = DataBindingUtil.inflate(inflater, R.layout.item_operation_card, parent, false);
        return new BindingHolder<>(binding);
    }

    @Override
    public void onBindViewHolder(BindingHolder<ItemOperationCardBinding> holder, int position) {
        final Operation operation = getItem(position);
        holder.binding.tvCategory.setText(operation.getCategory().getName());
        holder.binding.tvDescription.setText(operation.getDescription());
        holder.binding.tvCost.setText(String.valueOf(operation.getCost()));
        holder.binding.tvCurrency.setText(operation.getCurrency());

        if (operation.getType() == OperationType.INCOME) {
            holder.binding.tvCost.setTextColor(context.getResources().getColor(R.color.green));
            holder.binding.tvCurrency.setTextColor(context.getResources().getColor(R.color.green));
        } else if (operation.getType() == OperationType.EXPENSE) {
            holder.binding.tvCost.setTextColor(context.getResources().getColor(R.color.red));
            holder.binding.tvCurrency.setTextColor(context.getResources().getColor(R.color.red));
        }
    }
}
