java
public static void fetchPermissions() {
        final PermissionManager pm = SyncUser.currentUser().getPermissionManager();
        // Retrieve
        pm.getPermissions(new PermissionManager.PermissionsCallback() {
            @Override
            public void onSuccess(RealmResults<Permission> permissions) {

                Log.i(TAG, "PRINTING ALL PERMISSIONS");
                printPermissions(permissions);

                // Change listeners can be registered
                // The PermissionManager keeps a reference to these as long as the Permission Manager
                // is open, so they do not risk getting GC'ed
                permissions.addChangeListener(new RealmChangeListener<RealmResults<Permission>>() {
                    @Override
                    public void onChange(RealmResults<Permission> permissions) {
                        Log.i(TAG, "CHANGED PERMISSIONS");
                        printPermissions(permissions);
                    }
                });
                pm.close();
            }

            @Override
            public void onError(ObjectServerError error) {
                // handle error
                Log.i(TAG, "Error fetching permissions");
                Log.i(TAG, error.toString());
                pm.close();
            }
        });
}
