@Parcel(value = Parcel.Serialization.FIELD, analyze = arrayOf(ImagesModel::class))
open class ImagesModel : RealmObject() {
    @ParcelPropertyConverter(ImageModelListParcelConverter::class)
    open var imagesList: RealmList<ImageModel>? = null
}

class ImageModelListParcelConverter : RealmListParcelConverter<ImageModel>() {

    override fun itemToParcel(input: ImageModel, parcel: android.os.Parcel) =
            parcel.writeParcelable(Parcels.wrap<ImageModel>(input), 0)

    override fun itemFromParcel(parcel: android.os.Parcel): ImageModel =
            Parcels.unwrap(parcel.readParcelable(ImageModel::class.java.classLoader))

}

abstract class RealmListParcelConverter<T : RealmObject> : CollectionParcelConverter<T, RealmList<T>>() {
    override fun createCollection(): RealmList<T> = RealmList()
}

@Parcel(value = Parcel.Serialization.BEAN, analyze = arrayOf(ImageModel::class))
open class ImageModel : RealmObject() {

    @PrimaryKey
    open var id: Long? = null
    open var image: String? = null
    open var name: String? = null
    open var fileName: String? = null

}
