java
public static void storeRestricted(PersonResponse personResponse) {
        Realm realm = Realm.getDefaultInstance();
        Owner owner = realm.where(Owner.class).findFirst();
        if (owner == null) {
            return;
        }
        owner.load();
        Log.d("@@@@", "storeRestricted: " + owner.isValid());
        realm.beginTransaction();
        owner.setRestricted(
                getRealmStringListFromStringList(realm, personResponse.getRestricted()));
        realm.copyToRealmOrUpdate(owner);
        realm.commitTransaction();
        realm.close();
    }


