java
public static Observable<Boolean> deleteFiles(final String album) {
        return Observable
                .create(new Observable.OnSubscribe<Boolean>() {
                    @Override
                    public void call(Subscriber<? super Boolean> subscriber) {
                        boolean result = true;
                        final Album target = Realm.getDefaultInstance().where(Album.class).equalTo("path", album).findFirst();
                        for (MediaItem item : target.getSelected()) {
                            if (!deleteFileImpl(target, item)) {
                                result=false;
                            }
                        }
                        Realm.getDefaultInstance().executeTransaction(new Realm.Transaction() {
                            @Override
                            public void execute(Realm realm) {
                                target.clearMediaSelection();
                                target.setLastModified(new File(target.getPath()).lastModified());
                                if (target.getMedia().size()<1) {
                                    realm.where(Album.class).equalTo("path", target.getPath()).findAll().deleteAllFromRealm();
                                }
                            }
                        });
                        subscriber.onNext(result);
                        subscriber.onCompleted();
                    }
                })
                .observeOn(AndroidSchedulers.mainThread())
                .subscribeOn(Schedulers.newThread());
    }
`

Kotlin
fun insertOrUpdate(taskQueue: TaskQueue?, realm: Realm?) {
  try {
    realm?.run {
      executeTransaction {
        taskQueue?.let {
          insertOrUpdate(it)
        }
      }
    }
  } catch (e: Exception) {
    if (DEBUG_ENABLED) {
      e.printStackTrace()
    }
    Crashlytics.logException(e)
  }
}


kotlin

open class TaskQueue(
    @PrimaryKey var id: String,
    var layers: RealmList<Layer> = RealmList()):
    RealmObject(), Parcelable {

  companion object {
    @JvmField val CREATOR = object : Parcelable.Creator<TaskQueue> {
      override fun createFromParcel(source: Parcel) = TaskQueue(source)
      override fun newArray(size: Int): Array<TaskQueue?> = arrayOfNulls(size)
    }
  }
  //...Parcel and more not important
}
`

