Issue.Code:
java

public class Asset extends RealmObject {
    @PrimaryKey
    private long id;
    private String macAddress;

    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public String getMacAddress() {
        return macAddress;
    }

    public void setMacAddress(String macAddress) {
        this.macAddress = macAddress;
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Asset asset = (Asset) o;
        return Objects.equals(macAddress, asset.macAddress);
    }

    @Override
    public int hashCode() {
        return Objects.hash(macAddress);
    }
}

public class AssetLocalRepository {
    public static void storeAssets(List<Asset> assets) {
        Log.d("realm", "size= " + assets.size());
        Realm realm = Realm.getDefaultInstance();
        realm.executeTransaction(realm1 -> realm1.copyToRealmOrUpdate(assets));
        Log.d("realm", "q ct= " + realm.where(Asset.class).count());
        realm.close();
    }
}


