        this.userName = userName;
    }
	
	public String getUserLocality() {
        return userLocality;
    }

    public void setUserLocality(String userLocality) {
        this.userLocality = userLocality;
    }
	
	public String getUserGender() {
        return userGender;
    }

    public void setUserGender(String userGender) {
        this.userGender = userGender;
    }

	private static RealmLocalObject relamIODTO;

    public void RealmLocalObject() {

    }

    public static synchronized RealmLocalObject getRealmSingletonInstance() {
