`
public abstract class MyBaseActivity extends AppCompatActivity {

    public RealmLocalObject mRealmObj;
    private static Realm mRealm = null;

    @Override
    protected void onCreate(final Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_PORTRAIT);
        /*get the singletone instanceof RealmClass*/
        mRealmObj = RealmLocalObject .getRealmSingletoneInstance();
    }
	
	public abstract void initViews();


    public Realm getRealmInstance() {
        if (mRealm == null) {
            RealmConfiguration realmConfig = new RealmConfiguration.Builder(this)
                    .name("testingrealm")
                    .modules(Realm.getDefaultModule(), new AllModulesClasses())
                    .deleteRealmIfMigrationNeeded()
                    .build();
            mRealm = Realm.getInstance(realmConfig);
        }
        return mRealm;
    }

    public RealmLocalObject getStoredDataFromLocally() {
        RealmLocalObject result = null;
        try {
            result = getRealmInstance().where(RealmLocalObject.class)
                    .findFirst();
        } catch (Exception e) {
			e.printStackTrace();
        }
        return result;
    }

    public void storeIntoRelamLocalData() {
            this.getRealmInstance().executeTransaction(new Realm.Transaction() {
                @Override
                public void execute(Realm realm) {
                    mRealmObj.setUserID(InstaConstant.PRIMARY_KEY_VALUE);
                    getRealmInstance().copyToRealmOrUpdate(mRealmObj);
                }
            });
    }

}
