public class RealmOverflowBugActivity extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_realm_overflow_bug);

        Realm.init(this);

        RealmConfiguration config1 = new RealmConfiguration.Builder().name("config1").deleteRealmIfMigrationNeeded().build();
        RealmConfiguration config2 = new RealmConfiguration.Builder().name("config2").deleteRealmIfMigrationNeeded().build();

        Realm realm1 = Realm.getInstance(config1);
        Realm realm2 = Realm.getInstance(config2);

        //Set up initial data to realm1
        realm1.beginTransaction();

        //Add data
        Parent parent = realm1.createObject(Parent.class, "parentKey");
        Child child = realm1.createObject(Child.class, "childKey");

        //Set relations
        parent.getChildList().add(child);
        child.setParent(parent);

        realm1.commitTransaction();


        //Insert object with relations from realm1 to realm2
        realm2.beginTransaction();
        realm2.insertOrUpdate(parent); //StackOverflowError! Using copyToRealmOrUpdate works.
        realm2.commitTransaction();

        realm1.close();
        realm2.close();
    }
}
