where

 java
    @Override
    public RealmList<T> persistOrUpdate(Realm realm, RealmList<T> list) {
        RealmList<T> realmList = new RealmList<>();
        for(T t : realm.copyToRealmOrUpdate(list)) {
            realmList.add(t);
        }
        return realmList;
    }

    @Override
    public void saveOrUpdate(Realm realm, T t) {
        realm.insertOrUpdate(t);
    }

    @Override
    public void saveOrUpdate(Realm realm, RealmList<T> list) {
        if(!list.isEmpty()) {
            realm.insertOrUpdate(list);
        }
    }


