 @Override
    public void onStart() {
        super.onStart();

        // get hold of realm
        realm = Realm.getDefaultInstance();
        evaluateFilterSelections(null);
    }


and closing it here:

 @Override
    public void onStop() {
        super.onStop();

        try {

            if (retailerResults != null && !realm.isClosed()) {
                retailerResults.removeChangeListener(retailerCallback);
            }

            if (industryResults != null && !realm.isClosed()) {
                industryResults.removeChangeListener(industryCallback);
            }

        } catch (Exception ex) {
            ex.printStackTrace();
        }

        //clear adapters
        mCustomerRecyclerView.setAdapter(null);
        realm.close();
    }


this is my `evaluateFilterSelections` method:


RealmResults<RetailerRoute> routes = realm.where(RetailerRoute.class)
                        .equalTo(getWeekday(), true).findAll();
                RealmQuery<Retailer> query = realm.where(Retailer.class);

                // filter
                if (!TextUtils.isEmpty(searchQuery)) {
                    query = query.contains("name", searchQuery, Case.INSENSITIVE)
                            .beginGroup();
                }

                if (routes.size() > 0 && routeFilter.size() > 0) {

                    for (int a = 0; a < routes.size(); a++) {

                        if (routeFilter.get(0).getTitle().equals(Constants.IN_ROUTE)) {

                            if (a > 0) {
                                query = query.or();
                            }

                            query = query.equalTo("id", routes.get(a).getRetailerId());

                        } else {
                            query = query.notEqualTo("id", routes.get(a).getRetailerId());
                        }
                    }
                }

                if (!TextUtils.isEmpty(searchQuery)) {
                    query = query.endGroup();
                }

 retailerResults = query.findAllAsync();
 retailerResults.addChangeListener(retailerCallback);


and my change listener:


private RealmChangeListener retailerCallback = new RealmChangeListener<RealmResults<Retailer>>() {

        @Override
        public void onChange(RealmResults<Retailer> results) {

            //update retailerAdapter
            retailerAdapter.update(results);
        }
    };


