public List<Photo> getMonthPhotos(Date date) {
    List<Photo> list = new LinkedList();
    try{
        Realm db = getRealm();
        RealmQuery<Photo> all = db.where(Photo.class);
        Log.d(TAG, "RealmQuery all.count: " + all.count());
        int minYear = all.greaterThan("timeTaken", LyveCloudProvider.NO_DATE).minimumDate("timeTaken").getYear();
        int maxYear = all.maximumDate("timeTaken").getYear();
        Calendar cal = Calendar.getInstance();
        cal.setTime(date);
        int month = cal.get(Calendar.MONTH);
        int day = cal.get(Calendar.DAY_OF_MONTH);
        for (int year = minYear; year < maxYear; year++) {
            // before this week
            Log.d(TAG, "getMonthPhotos: in loop.. year of\t" + year);
            long t0 = SystemClock.currentThreadTimeMillis();
            Date begin = new Date(year, month, 1);      // first day of the month is 1, not 0.
            Date today = new Date(year, month, day);
            Date weekBegin = new Date(today.getTime() - DAY_IN_MS * 3 - 1);
            if (begin.getTime() < weekBegin.getTime()) {
                RealmResults<Photo> beforePhotos = all.between("timeTaken", begin, weekBegin).findAll();
                list.addAll(beforePhotos);
                Log.d(TAG, "It took " + (SystemClock.currentThreadTimeMillis() - t0)
                        + " ms to query photos between " + begin + " and " + weekBegin
                        + ", of year " + year + ". Photos amount: "
                        + ((beforePhotos == null) ? 0 : beforePhotos.size()));
            }
            // after this week
            Date weekEnd = new Date(today.getTime() + DAY_IN_MS * 4);
            int daysInMonth = new GregorianCalendar(1900 + year, month, day).getActualMaximum(Calendar.DAY_OF_MONTH);
            Date end = new Date(begin.getTime() + DAY_IN_MS * daysInMonth - 1);
            if (weekEnd.getTime() < end.getTime()) {
                RealmResults<Photo> dayPhotos = all.between("timeTaken", weekEnd, end).findAll();
                list.addAll(dayPhotos);
                Log.d(TAG, "It took " + (SystemClock.currentThreadTimeMillis() - t0)
                        + " ms to query photos between " + weekEnd + " and " + end
                        + ", of year " + year + ". Photos amount: "
                        + ((dayPhotos == null) ? 0 : dayPhotos.size()));
            }
        }
    } catch (Exception ex) {
        ex.printStackTrace();
    } finally {
        if (db != null) {
            db.close();
        }
    }
    return list;
}


