The sample below is stripped version of a real project but still we're able to reproduce it with it.
#### Code Sample

 java

public class MainActivity extends AppCompatActivity implements View.OnClickListener {

    private int totalCompletes;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        findViewById(R.id.refresh_tasks).setOnClickListener(this);
        RealmConfiguration config = new RealmConfiguration.Builder(this)
                .schemaVersion(BuildConfig.VERSION_CODE)
                .deleteRealmIfMigrationNeeded()
                .build();
        Realm.setDefaultConfiguration(config);
        totalCompletes = 0;
    }

    private void refreshTasks(final int callNum) {
        List<Task> tasks = new ArrayList<>();
        for (int i = 0; i < 1000; i++) {
            tasks.add(new Task(UUID.randomUUID().toString(), UUID.randomUUID().toString()));
        }
        saveTasks(tasks).flatMap(new Func1<List<Task>, Observable<List<Task>>>() {
            @Override
            public Observable<List<Task>> call(List<Task> tasks) {
                for (Task t : tasks) {
                    t.setName(UUID.randomUUID().toString());
                }
                return saveTasks(tasks);
            }
        }).flatMap(new Func1<List<Task>, Observable<?>>() {
            @Override
            public Observable<List<Task>> call(List<Task> tasks) {
                return findTasks();
            }
        }).subscribe(new Action1<Object>() {
            @Override
            public void call(Object o) {
                Log.d("RXTestRealm", "onNext");
            }
        }, new Action1<Throwable>() {
            @Override
            public void call(Throwable throwable) {
                Log.d("RXTestRealm", "onError " + throwable.getMessage());
            }
        }, new Action0() {
            @Override
            public void call() {
                totalCompletes++;
                Log.d("RXTestRealm", "onComplete: call " + callNum + " total completes: " + totalCompletes);
            }
        });
    }

    private Observable<List<Task>> findTasks() {
        final Realm realm = Realm.getDefaultInstance();
        return realm.where(Task.class).findAllAsync().asObservable().filter(new Func1<RealmResults<Task>, Boolean>() {
            @Override
            public Boolean call(RealmResults<Task> tasks) {
                return tasks.isLoaded();
            }
        }).first().map(new Func1<RealmResults<Task>, List<Task>>() {
            @Override
            public List<Task> call(RealmResults<Task> tasks) {
                List<Task> res = realm.copyFromRealm(tasks);
                realm.close();
                return res;
            }
        });
    }

    private Observable<List<Task>> saveTasks(final List<Task> tasks) {
        return Observable.create(new Observable.OnSubscribe<List<Task>>() {
            @Override
            public void call(final Subscriber<? super List<Task>> subscriber) {
                final Realm realm = Realm.getDefaultInstance();
                realm.executeTransactionAsync(new Realm.Transaction() {
                    @Override
                    public void execute(Realm backgroundRealm) {
                        backgroundRealm.copyToRealmOrUpdate(tasks);
                    }
                }, new Realm.Transaction.OnSuccess() {
                    @Override
                    public void onSuccess() {
                        realm.close();
                        subscriber.onNext(tasks);
                        subscriber.onCompleted();
                    }
                }, new Realm.Transaction.OnError() {
                    @Override
                    public void onError(Throwable error) {
                        realm.close();
                        subscriber.onError(error);
                    }
                });
            }
        });
    }

    @Override
    public void onClick(View v) {
        for(int i = 0; i < 10; i++) {
            refreshTasks(i);
        }
    }
}



