public class Task extends RealmObject {


    @PrimaryKey
    @Required
    private String id;

    public void setName(String name) {
        this.name = name;
    }

    private String name;

    public Task() {

    }

    public Task(String id, String name) {
        this.id = id;
        this.name = name;
    }

    public String getName() {
        return name;
    }
}


