 java
public Realm getTempLocalRealm()
    {
        Realm res = null;

        Context cntx = ConcurrencyController.getContextRealmOfThread() != null ? ConcurrencyController.getContextRealmOfThread() : this.context;
        String dbDir = ConcurrencyController.getRealmDirOfThread() != null ? ConcurrencyController.getRealmDirOfThread() : this.getDbDir();

        try
        {

            File dir = cntx.getDir(dbDir, Context.MODE_PRIVATE);
            // Create the Realm configuration
            RealmConfiguration realmConfig = new RealmConfiguration.Builder(dir).build();

            // Open the Realm for the thread.
            res = Realm.getInstance(realmConfig);
            setCounter(1);

        }
        catch (Exception e)
        {
            e.printStackTrace();

        }
        return res;
    }
