 java
public void markAllFilesFinished() {
        Log.d("REALM-TRACE", "in markAllFilesFinished");
        mRealm.beginTransaction();
        final RealmResults<RealmDataFile> results =
                mRealm.where(RealmDataFile.class).equalTo(RealmDataFile.FINISHED, false).findAll();
        Log.d("REALM-TRACE", "Results: " + results.size());

        for (final RealmDataFile result : results) {
            Log.d("REALM-TRACE", "setting file finished: " + result.getId());
            result.setFinished(true);
            Log.d("REALM-TRACE", "set file finished: " + result.getId());
        }

        Log.d("REALM-TRACE", "Commiting transaction.");
        mRealm.commitTransaction();
        Log.d("REALM-TRACE", "Exiting markAllFilesFinished.");
}


