 java
/**
 * A model for completed file meta data.
 */
@SuppressWarnings(
        {"checkstyle:membername", "checkstyle:designforextension", "checkstyle:javadocvariable"})
public class RealmDataFile extends RealmObject {

    @Ignore public static final String ID = "id";
    @Ignore public static final String NODE_ID = "nodeId";
    @Ignore public static final String START = "start";
    @Ignore public static final String END = "end";
    @Ignore public static final String LIVE = "live";
    @Ignore public static final String FINISHED = "finished";
    @Ignore public static final String UPLOADED = "uploaded";

    private long id;
    private String nodeId;
    private long start;
    private long end;
    private boolean live;
    private boolean finished;
    private boolean uploaded;

    public long getId() { return id; }
    public void setId(final long aId) { id = aId; }
    public String getNodeId() { return nodeId; }
    public void setNodeId(final String aNodeId) { nodeId = aNodeId; }
    public long getStart() { return start; }
    public void setStart(final long aStart) { start = aStart; }
    public long getEnd() { return end; }
    public void setEnd(final long aEnd) { end = aEnd; }
    public boolean isLive() { return live; }
    public void setLive(final boolean aLive) { live = aLive; }
    public boolean isFinished() { return finished; }
    public void setFinished(final boolean aFinished) { finished = aFinished; }
    public boolean isUploaded() { return uploaded; }
    public void setUploaded(final boolean aUploaded) { uploaded = aUploaded; }
}
