 javascript
    public void onClickFavorite(IFavorite iFavorite) {
        boolean favorite = !iFavorite.isFavorite();
        iFavorite.setFavorite(favorite);
        if (iFavorite == null) return;
        Realm realm = Realm.getInstance(context, CITY.getRealmFileName());
        if (realm == null) return;
        if (iFavorite instanceof Route) {
            RealmRoute realmRoute = realm.where(RealmRoute.class).equalTo("id", String.valueOf(iFavorite.getId())).findFirst();
            if (realmRoute == null) return;
            realm.beginTransaction();
            realmRoute.setFavorite(favorite);
            realm.commitTransaction();
            return;
        } else if (iFavorite instanceof Stop) {
            RealmStop realmStop = realm.where(RealmStop.class).equalTo("id", String.valueOf(iFavorite.getId())).findFirst();
            if (realmStop == null) return;
            realm.beginTransaction();
            realmStop.setFavorite(favorite);
            realm.commitTransaction();
            return;
        }

    }



    favorite.setOnClickListener(new View.OnClickListener() {
        @Override
        public void onClick(View view) {
            onClickFavorite(iFavorite);
            if (view instanceof ImageView) {
                view.startAnimation(AnimationUtils.loadAnimation(context, R.anim.anim_scale));
                favorite.setImageDrawable(context.getResources().getDrawable(iFavorite.isFavorite() ? R.drawable.ic_star_full : R.drawable.ic_star_empty));
            }
        }
    });
