 java
public void markAllFilesFinished() {
        mRealm.beginTransaction();
        final RealmResults<RealmDataFile> results =
                mRealm.where(RealmDataFile.class).equalTo(RealmDataFile.FINISHED, false).findAll();

        for (final RealmDataFile result : results) {
            result.setFinished(true);
        }

        mRealm.commitTransaction(); // this is line 296
    }
