cs
void OnInvocationExpression(node) {
    if (node.Identifier == "nameof") {
        if (TARGET_CS6_OR_ABOVE) {
            /* nameof must be treated as KEYWORD */ 
        }
        else {
           /* nameof must be treated as FUNCTION NAME */
        }
    }
    else 
         ; // Usual invocation syntax
}


