csharp
 public static byte[] Combine(this byte[] first, byte[] second)
        {
            first ??= new byte[0];
            second ??= new byte[0];

            var ret = new byte[first.Length + second.Length];
            Buffer.BlockCopy(first, 0, ret, 0, first.Length);
            Buffer.BlockCopy(second, 0, ret, first.Length, second.Length);
            return ret;
        }
