class Program
{
    static void DoSomething<T>() where T : unmanaged { }
    struct MyStruct {  public (int, int) Value; }
    static void Main(string[] args)
    {
        DoSomething<MyStruct>();    //  OK
        DoSomething<(int, int)>();  //  Shows compile-time error
    }
}
