c#
#nullable enable

abstract class A<T>
{
    internal abstract void F<U>(T t) where U : T;
}

class B : A<int?>
{
    internal override void F<U>(int? t)
    {
        U u = (U)(object?)t;
        u.ToString(); // may be null
    }
}
