        {
            var s = "1";
            if (true)
                switch (s)
                {
                    case "1":
                        Console.Out.WriteLine("Input was 1");
                        break;
                    default:
                        throw new Exception("Default case");
                }
            else
                Console.Out.WriteLine("Too many inputs");
        }
    }
}
